mapper.select:
SELECT m1.id AS id, m1.icon AS icon, ( CASE WHEN (m2.id = 0 OR m2.id IS NULL) THEN 0 ELSE m2.id END ) AS parentId, m1. NAME AS NAME, m1.url AS url, m1.levels AS levels, m1.ismenu AS ismenu, m1.num AS num, m1. CODE AS CODE, m1. STATUS AS STATUS, m1.component, IFNULL(m1.hidden, 0) AS hidden FROM t_sys_menu m1 LEFT JOIN t_sys_menu m2 ON m1.pcode = m2.code WHERE m1.id IN ( SELECT DISTINCT (tsr.menuid) FROM t_sys_relation tsr WHERE
#{id}
) ORDER BY levels, num ASC
